import React from "react";

const { useState, useEffect, useRef } = React;

const TreeNode = ({ node, level = 0, type = 'folder' }) => {
    const [expanded, setExpanded] = useState(false);
    const [showDetails, setShowDetails] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);

    const handleToggle = () => {
        setExpanded(!expanded);
    };

    const hasChildren = node.children && node.children.length > 0;
    const hasFunctions = node.functions && node.functions.length > 0;
    const hasModules = node.modules && node.modules.length > 0;

    return (
        <div className="tree-node">
            <div
                className={`tree-item ${expanded ? 'expanded' : ''} ${isSelected ? 'selected' : ''}`}
                onClick={isSelectable ? handleItemClick : handleToggle}
            >
                <div className="tree-toggle" onClick={handleToggle}>
                    {(hasChildren || hasFunctions || hasModules) ? (expanded ? '‚ñº' : '‚ñ∂') : ''}
                </div>
                <div className={`tree-icon ${type === 'function' ? 'function' : type === 'module' ? 'item' : type}`}>
                    {type === 'folder' ? 'üìÅ' :
                        type === 'module' ? '‚öôÔ∏è' :
                            type === 'function' ? '‚ö°' : 'üìÑ'}
                </div>
                <div className="tree-label">
                    {node.name || node.type || node.moduleType}
                </div>
                {node.status && (
                    <div className={`tree-status ${node.status}`}>
                        {node.status === 'modified' ? '–ò–∑–º–µ–Ω–µ–Ω' :
                            node.status === 'added' ? '–î–æ–±–∞–≤–ª–µ–Ω' : '–£–¥–∞–ª–µ–Ω'}
                    </div>
                )}
            </div>

            {expanded && (
                <div className="tree-children">
                    {hasChildren && node.children.map((child, index) => (
                        <TreeNode
                            key={index}
                            node={child}
                            level={level + 1}
                            type="item"
                            onItemSelect={onItemSelect}
                            selectedItemId={selectedItemId}
                        />
                    ))}
                    {hasModules && node.modules.map((module, index) => (
                        <TreeNode
                            key={index}
                            node={module}
                            level={level + 1}
                            type="module"
                            onItemSelect={onItemSelect}
                            selectedItemId={selectedItemId}
                        />
                    ))}
                    {hasFunctions && node.functions.map((func, index) => (
                        <TreeNode
                            key={index}
                            node={func}
                            level={level + 1}
                            type="function"
                            onItemSelect={onItemSelect}
                            selectedItemId={selectedItemId}
                        />
                    ))}
                </div>
            )}
        </div>
    );

};

export default TreeNode;
