import React from "react";
import { startManualAnalysis} from "../services/cron";
import {useMutation, useQueryClient} from '@tanstack/react-query';
import {useWebSocket} from '../services/contexWebSocket'

const { useState, useEffect, useRef } = React;

function useLogs(dbId) {
    const { message } = useWebSocket();
    const [logs, setLogs] = useState([]);

    useEffect(() => {
        try {
            const obj = JSON.parse(message);

            // фильтруем по типу и ID
            if (obj.type === "log" && obj.db_id === dbId) {
                setLogs((prev) => [...prev, obj.data || message]);
            }
        } catch (e) {
            console.error("Invalid WS message:", message);
        }
    }, [message, dbId]);

    return logs;
}

function useProgress() {
    const [logs, setLogs] = useState([]);

    return
}

const AnalysisView = ({ database, onComplete, onCancel }) => {
    const [error, setError] = useState('');
    //const [currentStep, setCurrentStep] = useState(0);
    const {message, _} = useWebSocket();
    const logs = useLogs(database.ID)
    const [showLogs, setShowLogs] = useState(false);
    const [progress, setProgress] = useState(0);
    const logEndRef = useRef(null);
    const queryClient = useQueryClient();
    const manualAnalysis = useMutation({
        mutationFn: (id) => startManualAnalysis(id),
        onError: (error) => {
            console.log(error)
            setError(error.message)
        },
        onSuccess: () => {
            setError('')
        },
    });


    useEffect(() => {
        manualAnalysis.mutate(database.ID);
    }, []); // [] — чтобы вызвалось только один раз при монтировании

    useEffect(() => {
        if (logEndRef.current) {
            logEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [message]);


    if (error) {
        return (
            <div className="empty-state">
                <div className="empty-state-icon">⛔</div>
                <h2>Произошла ошибка</h2>
                <p>{error}</p>
            </div>
        );
    }

    queryClient.invalidateQueries({
        queryKey: ['databases']
    }).then(r => {});


    const steps = [
        "Подключение к серверу 1С...",
        "Выгрузка конфигурации...",
        "Анализ расширений...",
        "Обработка метаданных..."
    ];

    return (
        <div className="analysis-view">
            <div className="analysis-status">
                <div className="spinner"></div>
                <div className="status-text">Анализ базы: {database.Name}</div>
                <div className="substatus-text">{logs[logs.length-1]}</div>
                {/*<div className="substatus-text">Прогресс: {progress}%</div>*/}
            </div>

            <div className="log-section">
                <div className="card">
                    <button
                        className="btn btn--secondary log-toggle"
                        onClick={() => setShowLogs(!showLogs)}
                    >
                        {showLogs ? 'Скрыть лог анализа' : 'Показать лог анализа'}
                    </button>

                    {showLogs && (
                        <div className="log-viewer">
                            {logs?.map((log, index) => (
                                <div key={index} className="log-entry">
                                    {log}
                                </div>
                            ))}
                            <div ref={logEndRef} />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AnalysisView;
