import { getBaseSettings, deleteBaseSettings } from '../services/settingservice';
import { useQuery } from '@tanstack/react-query';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { useDatabase } from "./DatabaseContext";
import React from "react";
import ScheduleEditorModal from "./CRONSchedule";

const getStatusText = (status) => {
    switch (status) {
        case 'new': return 'Новая';
        case 'analyzing': return 'Анализ';
        case 'done': return 'Готово';
        case 'error': return 'Ошибка';
        default: return 'Неизвестно';
    }
};

function useDeleteBaseSettings() {
    const queryClient = useQueryClient();

    return useMutation({
        mutationFn: (id) => deleteBaseSettings(id),
        onSuccess: () => {
            // после успешного удаления — обновим кэш
            queryClient.invalidateQueries({
                queryKey: ['databases']
            }).then(r => {});
        },
    });
}

const DatabaseList = () => {
    const { _, setSelectedDb } = useDatabase();
    const {useState, useEffect, useRef} = React;
    const { data: databases, isLoading, error } = useQuery({
        queryKey: ['databases'],
        queryFn: getBaseSettings,
        select: (data) => data.data,
    });
    if(error) {
        console.log(error)
    }

    const [activeDatabaseId, setActiveDatabaseId] = useState(null);

    useEffect(() => {
        if (databases && databases.length > 0) {
            setActiveDatabaseId(databases[0].ID);
        }
    }, [databases]); // срабатывает когда databases загружаются

    return(
        <div className="database-list">
            { databases && databases.map((db) => (
                    <DatabaseItem
                        key={db.ID}
                        database={db}
                        isActive={db.ID === activeDatabaseId}
                        setActiveDatabase={(db) => {
                            setActiveDatabaseId(db.ID);
                            setSelectedDb(db);
                        }
                    } />
                ))
            }
        </div>
    )
}

const DatabaseItem = ({database, isActive, setActiveDatabase}) => {
    const queryClient = useQueryClient();

    const {useState, useEffect, useRef} = React;
    const [scheduleModalIsShow, setShowScheduleModal] = useState(false);
    const handleStartAnalysis = (e,database) => {
        e.stopPropagation();

        setActiveDatabase({...database, Status: 'analyzing'})
        queryClient.invalidateQueries({
            queryKey: ['databases']
        }).then(r => {});
    };
    const deleteMutation = useDeleteBaseSettings();
    const onClick = (database) => {
        setActiveDatabase(database.ID)
    }

    return (
        <div
            className={`database-item ${isActive ? 'active' : ''}`}
            onClick={() => onClick(database)}
        >
            <div className="database-actions">
                <button
                    className="close-btn"
                    onClick={(e) => {
                        const confirmDelete = window.confirm("Вы уверены, что хотите удалить запись?");
                        if (!confirmDelete) {
                            return
                        }

                        e.stopPropagation();
                        deleteMutation.mutate(database.ID);
                    }}
                    title="Удалить базу"
                >
                    ×
                </button>
            </div>

            <div className="database-item-header">
                <div className="database-name">{database.Name}</div>
                <div className="database-status">
                    <div className={`status-indicator ${database.Status}`}></div>
                    <span>{getStatusText(database.Status)}</span>
                </div>
            </div>

            {!database.Cron && (
                <div className="schedule-status">
                <div className="schedule-icon">⏸️</div>
                <span>Только ручной запуск</span></div>)}

            <div className="database-details">
                {database.ConnectionString}
                {database.Cron && database.Cron.LastCheckAsString && (
                    <div>Последний анализ: {database.Cron.LastCheckAsString}</div>
                )}
            </div>

            {database.Status === 'analyzing' && database.progress && (
                <div className="database-progress">
                    <div
                        className="progress-fill"
                        style={{width: `${database.progress}%`}}
                    ></div>
                </div>
            )}

            {database.Status !== 'analyzing' && (
                <div style={{display: 'flex', gap: '8px'}}>
                    <button
                        className="btn btn--primary btn--sm"
                        style={{marginTop: '8px', width: '100%'}}
                        onClick={() => setShowScheduleModal(true)}
                    >
                        Расписание
                    </button>
                    <button className="btn btn--primary btn--sm"
                            onClick={(e) => handleStartAnalysis(e, database)}
                            style={{marginTop: '8px', width: '100%'}}>
                        Начать анализ
                    </button>
                </div>
            )
            }

            {scheduleModalIsShow && (<ScheduleEditorModal
                database={database}
                onClose={() => setShowScheduleModal(false)}
            />)}
        </div>
    );
};

export default DatabaseList;
