import { getBaseSettings, deleteBaseSettings } from '../services/settingservice';
import { useQuery } from '@tanstack/react-query';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import React from "react";

const getStatusText = (status) => {
    switch (status) {
        case 'new': return 'Новая';
        case 'analyzing': return 'Анализ';
        case 'done': return 'Готово';
        case 'error': return 'Ошибка';
        default: return 'Неизвестно';
    }
};

function useDeleteBaseSettings() {
    const queryClient = useQueryClient();

    return useMutation({
        mutationFn: (id) => deleteBaseSettings(id),
        onSuccess: () => {
            // после успешного удаления — обновим кэш
            queryClient.invalidateQueries({
                queryKey: ['databases']
            }).then(r => {});
        },
    });
}

const DatabaseList = () => {
    const { data: databases, isLoading, error } = useQuery({
        queryKey: ['databases'],
        queryFn: getBaseSettings,
        select: (data) => data.data,
    });
    if(error) {
        console.log(error)
    }


    console.log(databases)

   //const [activeDatabaseId, setActiveDatabaseId] = useState('db_1');

    return(
        <div className="database-list">
            { databases && databases.map((db) => (
                    <DatabaseItem
                        key={db.ID}
                        database={db}
                        //isActive={database.ID === activeDatabaseId}
                        // onRemove={removeDatabase}
                        // onStartAnalysis={startAnalysis}
                        />
                ))
            }
        </div>
    )
}

const DatabaseItem = ({database, isActive, onStartAnalysis}) => {
    const handleStartAnalysis = (e) => {
        e.stopPropagation();
        onStartAnalysis(database.ID);
    };

    const onClick = () => {

    }

    const onRemove = (id) => {

    }


    return (
        <div
            className={`database-item ${isActive ? 'active' : ''}`}
            onClick={() => onClick(database.ID)}
        >
            <div className="database-actions">
                <button
                    className="remove-btn"
                    onClick={(e) => {
                        e.stopPropagation();
                        onRemove(database.ID);
                    }}
                    title="Удалить базу"
                >
                    ×
                </button>
            </div>

            <div className="database-item-header">
                <div className="database-name">{database.Name}</div>
                <div className="database-status">
                    <div className={`status-indicator ${database.Status}`}></div>
                    <span>{getStatusText(database.Status)}</span>
                </div>
            </div>

            <div className="database-details">
                {database.ConnectionString}
                {database.lastAnalysis && (
                    <div>Анализ: {database.lastAnalysis}</div>
                )}
            </div>

            {database.Status === 'analyzing' && database.progress && (
                <div className="database-progress">
                    <div
                        className="progress-fill"
                        style={{ width: `${database.progress}%` }}
                    ></div>
                </div>
            )}

            {database.Status === 'connected' && (
                <button
                    className="btn btn--primary btn--sm"
                    onClick={handleStartAnalysis}
                    style={{ marginTop: '8px', width: '100%' }}
                >
                    Начать анализ
                </button>
            )}
        </div>
    );
};

export default DatabaseList;
