    ],
    overriddenFunctions: [
      {
        moduleType: "–û–±—â–∏–µ –º–æ–¥—É–ª–∏",
        modules: [
          {
            name: "–†–∞–±–æ—Ç–∞–°–§–∞–π–ª–∞–º–∏",
            functions: [
              {name: "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å–§–∞–π–ª–ù–∞–î–∏—Å–∫", status: "modified"},
              {name: "–ü–æ–ª—É—á–∏—Ç—å–ü—É—Ç—å–ö–§–∞–π–ª—É", status: "modified"}
            ]
