import {apiRequest} from './request'

// Получить настройки CRON
export const getCronSettings = () => {
    return apiRequest(`/getCronSettings`);
};

// Сохранить настройки CRON
export const setCronSettings = ({bd_id, data}) => {
    return apiRequest(`/setCronSettings/${bd_id}`, { method: 'POST', body: JSON.stringify(data)});
};

// Удаление настройки CRON
export const deleteCronSettings = ({bd_id}) => {
    return apiRequest(`/deleteCronSettings/${bd_id}`, { method: 'DELETE'});
};
