import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { getBaseSettings } from '../services/settingservice';
import UserCard from '../components/UserCard';
import UserModal from '../components/UserModal';
import './Settings.css';


import { Dropdown } from 'react-bootstrap';
import { DropdownButton} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


const Settings = () => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  //const [isOpen, setIsOpen] = useState(false);

  const { data: settings, isLoading, error } = useQuery({
    queryKey: ['settings'],
    queryFn: getBaseSettings,
    select: (data) => data.data,
  });

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleCreateUser = () => {
    setSelectedUser(null);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  const handleOption1 = () => {
    alert("Создать PostgreSQL базу");
    //setIsOpen(false); // закрыть меню после клика
  };

  const handleOption2 = () => {
    alert("Создать MySQL базу");
   // setIsOpen(false);
  };


  if (isLoading) {
    return <div className="loading">Загрузка настроек...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error.message}</div>;
  }

  return (
    <div className="settings-page">

      <DropdownButton id="dropdown-basic-button" title="Dropdown button">
        <Dropdown.Item onClick={ () =>  {}}>Another action</Dropdown.Item>
        <Dropdown.Item onClick={ () =>  {}}>Something else</Dropdown.Item>
      </DropdownButton>



      {/*<div className="settings-header">*/}
      {/*  <h2>Подключение к базам</h2>*/}
      {/*  <button onClick={ () =>  setIsOpen(!isOpen)} className="btn btn-primary">*/}
      {/*    Добавить базу*/}
      {/*  </button>*/}
      {/*</div>*/}



      <div className="settings-list">
        {settings && settings.length > 0 ? (
          settings.map((user) => (
            <UserCard
              key={user.id}
              user={user}
              onEdit={handleEditUser}
            />
          ))
        ) : (
          <div className="no-settings">
            <p>Настройки не найдены</p>
          </div>
        )}
      </div>

      {isModalOpen && (
        <UserModal
          user={selectedUser}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default Settings;
