import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';


const Home = () => {
  return (
      <div className="app">
        <header className="header">
          <div className="container">
            <div className="header-content">
              <h1 className="logo">Анализатор базы данных 1С</h1>
              <div className="header-right">
                <div className="database-counter">
                  Баз данных: {databases.length}
                </div>
                <ThemeToggle theme={theme} onToggle={toggleTheme} />
              </div>
            </div>
          </div>
        </header>

        <div className="main-layout">
          <aside className="sidebar">
            <div className="sidebar-header">
              <h2 className="sidebar-title">Базы данных</h2>
              <button
                  className="add-database-btn"
                  onClick={() => setShowModal(true)}
              >
                <span>+</span>
                Добавить базу
              </button>
            </div>

            <div className="sidebar-content">
              <div className="database-list">
                {databases.map(database => (
                    <DatabaseItem
                        key={database.id}
                        database={database}
                        isActive={database.id === activeDatabaseId}
                        onClick={setActiveDatabaseId}
                        onRemove={removeDatabase}
                        onStartAnalysis={startAnalysis}
                    />
                ))}
              </div>
            </div>
          </aside>

          <main className="main-content">
            {renderMainContent()}
          </main>
        </div>

        <DatabaseFormModal
            isOpen={showModal}
            onClose={() => setShowModal(false)}
            onSubmit={addDatabase}
        />
      </div>
  );
};

export default Home;
