import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';


const { useState, useEffect, useRef } = React;

// Sample databases from provided data
const sampleDatabases = [
  {
    id: "db_1",
    name: "–£–¢ 11.4.16.242",
    connectionString: "Srvr=srv-1c-01;Ref=UT_Base;Usr=admin;Pwd=***;",
    server: "srv-1c-01",
    database: "UT_Base",
    status: "completed",
    lastAnalysis: "2024-10-04 10:45:00",
    configuration: {
      name: "–£–¢ 11.4.16.242",
      version: "11.4.16.242",
      extensions: ["–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ü–æ–¥–∫–ª—é—á–∞–µ–º–æ–≥–æ–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è", "–ò–Ω—Ç–µ–≥—Ä–∞—Ü–∏—è–°1–°–î–æ–∫—É–º–µ–Ω—Ç–æ–æ–±–æ—Ä–æ—Ç", "–ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏"]
    },
    overriddenMetadata: [
      {
        extensionName: "–ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏",
        items: [
          {
            type: "–°–ø—Ä–∞–≤–æ—á–Ω–∏–∫–∏",
            children: [
              {name: "–ù–æ–º–µ–Ω–∫–ª–∞—Ç—É—Ä–∞", status: "modified", changes: ["–î–æ–±–∞–≤–ª–µ–Ω —Ä–µ–∫–≤–∏–∑–∏—Ç –í–Ω–µ—à–Ω–∏–π–ö–æ–¥", "–ò–∑–º–µ–Ω–µ–Ω–∞ —Ñ–æ—Ä–º–∞ —ç–ª–µ–º–µ–Ω—Ç–∞"]},
              {name: "–ö–æ–Ω—Ç—Ä–∞–≥–µ–Ω—Ç—ã", status: "modified", changes: ["–î–æ–±–∞–≤–ª–µ–Ω–∞ —Ç–∞–±–ª–∏—á–Ω–∞—è —á–∞—Å—Ç—å –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ–†–µ–∫–≤–∏–∑–∏—Ç—ã"]}
            ]
          },
          {
            type: "–î–æ–∫—É–º–µ–Ω—Ç—ã",
            children: [
              {name: "–ü—Ä–∏—Ö–æ–¥–Ω–∞—è–ù–∞–∫–ª–∞–¥–Ω–∞—è", status: "modified", changes: ["–ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∞ –ø—Ä–æ—Ü–µ–¥—É—Ä–∞ –ü—Ä–∏–ó–∞–ø–∏—Å–∏"]},
              {name: "–†–∞—Å—Ö–æ–¥–Ω–∞—è–ù–∞–∫–ª–∞–¥–Ω–∞—è", status: "added", changes: ["–ù–æ–≤—ã–π –¥–æ–∫—É–º–µ–Ω—Ç –¥–ª—è —É—á–µ—Ç–∞ —Ä–∞—Å—Ö–æ–¥–æ–≤"]}
            ]
          }
        ]
      }
    ],
    overriddenFunctions: [
      {
        moduleType: "–û–±—â–∏–µ –º–æ–¥—É–ª–∏",
        modules: [
          {
            name: "–†–∞–±–æ—Ç–∞–°–§–∞–π–ª–∞–º–∏",
            functions: [
              {name: "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å–§–∞–π–ª–ù–∞–î–∏—Å–∫", status: "modified"},
              {name: "–ü–æ–ª—É—á–∏—Ç—å–ü—É—Ç—å–ö–§–∞–π–ª—É", status: "modified"}
            ]
          }
        ]
      }
    ]
  },
  {
    id: "db_2",
    name: "ERP 3.0.74.53",
    connectionString: "Srvr=srv-1c-02;Ref=ERP_Base;Usr=user;Pwd=***;",
    server: "srv-1c-02",
    database: "ERP_Base",
    status: "analyzing",
    progress: 65,
    currentStep: "–ê–Ω–∞–ª–∏–∑ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π...",
    configuration: {
      name: "ERP 3.0.74.53",
      version: "3.0.74.53",
      extensions: ["–ò–Ω—Ç–µ–≥—Ä–∞—Ü–∏—è1–°–î–æ–∫—É–º–µ–Ω—Ç–æ–æ–±–æ—Ä–æ—Ç", "–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ë—é–¥–∂–µ—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ"]
    }
  },
  {
    id: "db_3",
    name: "–†–æ–∑–Ω–∏—Ü–∞ 2.3.15.128",
    connectionString: "Srvr=srv-1c-03;Ref=Retail_Base;Usr=manager;Pwd=***;",
    server: "srv-1c-03",
    database: "Retail_Base",
    status: "connected",
    configuration: {
      name: "–†–æ–∑–Ω–∏—Ü–∞ 2.3.15.128",
      version: "2.3.15.128",
      extensions: ["–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ü–æ–¥–∫–ª—é—á–∞–µ–º–æ–≥–æ–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è", "–ö–∞—Å—Å–æ–≤–∞—è–°–º–µ–Ω–∞"]
    }
  }
];

const analysisLog = [
  "–ù–∞—á–∞–ª–æ –∞–Ω–∞–ª–∏–∑–∞ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö",
  "–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É 1–°...",
  "–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ",
  "–í—ã–≥—Ä—É–∑–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏...",
  "–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –≤—ã–≥—Ä—É–∂–µ–Ω–∞ (–£–¢ 11.4.16.242)",
  "–ü–æ–∏—Å–∫ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—ã—Ö —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π...",
  "–ù–∞–π–¥–µ–Ω–æ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π: 3",
  "–ê–Ω–∞–ª–∏–∑ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏—è: –ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏",
  "–ù–∞–π–¥–µ–Ω–æ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –æ–±—ä–µ–∫—Ç–æ–≤: 5",
  "–ê–Ω–∞–ª–∏–∑ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –ø—Ä–æ—Ü–µ–¥—É—Ä –∏ —Ñ—É–Ω–∫—Ü–∏–π...",
  "–ù–∞–π–¥–µ–Ω–æ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –º–µ—Ç–æ–¥–æ–≤: 6",
  "–ê–Ω–∞–ª–∏–∑ –∑–∞–≤–µ—Ä—à–µ–Ω —É—Å–ø–µ—à–Ω–æ"
];


// Theme Toggle Component
const ThemeToggle = ({ theme, onToggle }) => {
  const icon = theme === 'light' ? 'üåô' : '‚òÄÔ∏è';

  return (
      <button
          className="theme-toggle"
          onClick={onToggle}
          title={theme === 'light' ? '–ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å –Ω–∞ —Ç—ë–º–Ω—É—é —Ç–µ–º—É' : '–ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å –Ω–∞ —Å–≤–µ—Ç–ª—É—é —Ç–µ–º—É'}
      >
        <span className="theme-icon">{icon}</span>
      </button>
  );
};

// TreeNode component for expandable tree structure
const TreeNode = ({ node, level = 0, type = 'folder' }) => {
  const [expanded, setExpanded] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  const handleToggle = () => {
    setExpanded(!expanded);
  };

  const hasChildren = node.children && node.children.length > 0;
  const hasFunctions = node.functions && node.functions.length > 0;
  const hasModules = node.modules && node.modules.length > 0;

  return (
      <div className="tree-node">
        <div className="tree-item" onClick={handleToggle}>
          <div className="tree-toggle">
            {(hasChildren || hasFunctions || hasModules) ? (expanded ? '‚ñº' : '‚ñ∂') : ''}
          </div>
          <div className={`tree-icon ${type}`}>
            {type === 'folder' ? 'üìÅ' : type === 'module' ? '‚öôÔ∏è' : 'üìÑ'}
          </div>
          <div className="tree-label">
            {node.name || node.type || node.moduleType}
          </div>
          {node.status && (
              <div className={`tree-status ${node.status}`}>
                {node.status === 'modified' ? '–ò–∑–º–µ–Ω–µ–Ω' :
                    node.status === 'added' ? '–î–æ–±–∞–≤–ª–µ–Ω' : '–£–¥–∞–ª–µ–Ω'}
              </div>
          )}
        </div>

        {expanded && (
            <div className="tree-children">
              {hasChildren && node.children.map((child, index) => (
                  <TreeNode key={index} node={child} level={level + 1} type="item" />
              ))}
              {hasModules && node.modules.map((module, index) => (
                  <TreeNode key={index} node={module} level={level + 1} type="module" />
              ))}
              {hasFunctions && node.functions.map((func, index) => (
                  <TreeNode key={index} node={func} level={level + 1} type="item" />
              ))}
            </div>
        )}

        {expanded && node.changes && (
            <div className="tree-details">
              <strong>–ò–∑–º–µ–Ω–µ–Ω–∏—è:</strong>
              <ul>
                {node.changes.map((change, index) => (
                    <li key={index}>{change}</li>
                ))}
              </ul>
            </div>
        )}
      </div>
  );
};

// Database Form Modal
const DatabaseFormModal = ({ isOpen, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    server: '',
    database: '',
    username: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim() || !formData.server.trim() || !formData.database.trim()) {
      setError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è');
      return;
    }

    const connectionString = `Srvr=${formData.server};Ref=${formData.database};Usr=${formData.username};Pwd=${formData.password};`;

    onSubmit({
      name: formData.name,
      server: formData.server,
      database: formData.database,
      connectionString
    });

    setFormData({ name: '', server: '', database: '', username: '', password: '' });
    setError('');
    onClose();
  };

  const handleClose = () => {
    setFormData({ name: '', server: '', database: '', username: '', password: '' });
    setError('');
    onClose();
  };

  if (!isOpen) return null;

  return (
      <div className="modal">
        <div className="modal-content">
          <div className="modal-header">
            <h2 className="modal-title">–î–æ–±–∞–≤–∏—Ç—å –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö</h2>
            <button className="close-btn" onClick={handleClose}>√ó</button>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">–ù–∞–∑–≤–∞–Ω–∏–µ –±–∞–∑—ã *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ—Ä–≥–æ–≤–ª–µ–π 11.4"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">–°–µ—Ä–≤–µ—Ä *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="srv-1c-01"
                  value={formData.server}
                  onChange={(e) => setFormData({ ...formData, server: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">–ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="UT_Base"
                  value={formData.database}
                  onChange={(e) => setFormData({ ...formData, database: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="admin"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">–ü–∞—Ä–æ–ª—å</label>
              <input
                  type="password"
                  className="connection-input"
                  placeholder="password"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              />
            </div>

            {error && <div style={{ color: 'var(--color-error)', marginBottom: '16px', fontSize: '14px' }}>{error}</div>}

            <div className="form-actions">
              <button type="button" className="btn btn--secondary" onClick={handleClose}>
                –û—Ç–º–µ–Ω–∞
              </button>
              <button type="submit" className="btn btn--primary">
                –î–æ–±–∞–≤–∏—Ç—å
              </button>
            </div>
          </form>
        </div>
      </div>
  );
};

// Database Item Component
const DatabaseItem = ({ database, isActive, onClick, onRemove, onStartAnalysis }) => {
  const getStatusText = () => {
    switch (database.status) {
      case 'connecting': return '–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ...';
      case 'connected': return '–ü–æ–¥–∫–ª—é—á–µ–Ω–∞';
      case 'analyzing': return '–ê–Ω–∞–ª–∏–∑...';
      case 'completed': return '–ó–∞–≤–µ—Ä—à–µ–Ω';
      case 'error': return '–û—à–∏–±–∫–∞';
      default: return '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ';
    }
  };

  const handleStartAnalysis = (e) => {
    e.stopPropagation();
    onStartAnalysis(database.id);
  };

  return (
      <div
          className={`database-item ${isActive ? 'active' : ''}`}
          onClick={() => onClick(database.id)}
      >
        <div className="database-actions">
          <button
              className="remove-btn"
              onClick={(e) => {
                e.stopPropagation();
                onRemove(database.id);
              }}
              title="–£–¥–∞–ª–∏—Ç—å –±–∞–∑—É"
          >
            √ó
          </button>
        </div>

        <div className="database-item-header">
          <div className="database-name">{database.name}</div>
          <div className="database-status">
            <div className={`status-indicator ${database.status}`}></div>
            <span>{getStatusText()}</span>
          </div>
        </div>

        <div className="database-details">
          {database.server} / {database.database}
          {database.lastAnalysis && (
              <div>–ê–Ω–∞–ª–∏–∑: {database.lastAnalysis}</div>
          )}
        </div>

        {database.status === 'analyzing' && database.progress && (
            <div className="database-progress">
              <div
                  className="progress-fill"
                  style={{ width: `${database.progress}%` }}
              ></div>
            </div>
        )}

        {database.status === 'connected' && (
            <button
                className="btn btn--primary btn--sm"
                onClick={handleStartAnalysis}
                style={{ marginTop: '8px', width: '100%' }}
            >
              –ù–∞—á–∞—Ç—å –∞–Ω–∞–ª–∏–∑
            </button>
        )}
      </div>
  );
};

// Analysis View Component
const AnalysisView = ({ database, onComplete, onCancel }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [logs, setLogs] = useState([]);
  const [showLogs, setShowLogs] = useState(false);
  const [progress, setProgress] = useState(0);
  const logEndRef = useRef(null);

  const steps = [
    "–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É 1–°...",
    "–í—ã–≥—Ä—É–∑–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏...",
    "–ê–Ω–∞–ª–∏–∑ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π...",
    "–û–±—Ä–∞–±–æ—Ç–∫–∞ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã—Ö..."
  ];

  useEffect(() => {
    console.log(`–ù–∞—á–∞–ª–æ –∞–Ω–∞–ª–∏–∑–∞ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö: ${database.name}`);

    const analysisTimer = setTimeout(() => {
      let logIndex = 0;
      const logInterval = setInterval(() => {
        if (logIndex < analysisLog.length) {
          setLogs(prev => [...prev, analysisLog[logIndex]]);
          setProgress(Math.floor((logIndex + 1) / analysisLog.length * 100));
          logIndex++;
        } else {
          clearInterval(logInterval);
          console.log(`–ê–Ω–∞–ª–∏–∑ –∑–∞–≤–µ—Ä—à–µ–Ω –¥–ª—è –±–∞–∑—ã: ${database.name}`);
          setTimeout(() => onComplete(database.id), 1000);
        }
      }, 300);

      const stepInterval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < steps.length - 1) {
            return prev + 1;
          } else {
            clearInterval(stepInterval);
            return prev;
          }
        });
      }, 800);

      return () => {
        clearInterval(logInterval);
        clearInterval(stepInterval);
      };
    }, 500);

    return () => clearTimeout(analysisTimer);
  }, [database.id]);

  useEffect(() => {
    if (logEndRef.current) {
      logEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [logs]);

  return (
      <div className="analysis-view">
        <div className="analysis-status">
          <div className="spinner"></div>
          <div className="status-text">–ê–Ω–∞–ª–∏–∑ –±–∞–∑—ã: {database.name}</div>
          <div className="substatus-text">{steps[currentStep]}</div>
          <div className="substatus-text">–ü—Ä–æ–≥—Ä–µ—Å—Å: {progress}%</div>
          <button className="btn btn--cancel" onClick={() => onCancel(database.id)}>
            –û—Ç–º–µ–Ω–∏—Ç—å –∞–Ω–∞–ª–∏–∑
          </button>
        </div>

        <div className="log-section">
          <div className="card">
            <button
                className="btn btn--secondary log-toggle"
                onClick={() => setShowLogs(!showLogs)}
            >
              {showLogs ? '–°–∫—Ä—ã—Ç—å –ª–æ–≥ –∞–Ω–∞–ª–∏–∑–∞' : '–ü–æ–∫–∞–∑–∞—Ç—å –ª–æ–≥ –∞–Ω–∞–ª–∏–∑–∞'}
            </button>

            {showLogs && (
                <div className="log-viewer">
                  {logs.map((log, index) => (
                      <div key={index} className="log-entry">
                        {log}
                      </div>
                  ))}
                  <div ref={logEndRef} />
                </div>
            )}
          </div>
        </div>
      </div>
  );
};

// Results View Component
const ResultsView = ({ database }) => {
  if (!database.overriddenMetadata || !database.overriddenFunctions) {
    return (
        <div className="empty-state">
          <div className="empty-state-icon">üìä</div>
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –∞–Ω–∞–ª–∏–∑–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω—ã</h2>
          <p>–î–∞–Ω–Ω—ã–µ –∞–Ω–∞–ª–∏–∑–∞ –¥–ª—è —ç—Ç–æ–π –±–∞–∑—ã –µ—â–µ –Ω–µ –≥–æ—Ç–æ–≤—ã.</p>
        </div>
    );
  }

  return (
      <div className="results-view">
        <div className="results-header">
          <h1>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –∞–Ω–∞–ª–∏–∑–∞: {database.name}</h1>
