import './Home.css';


const { useState, useEffect, useRef } = React;

// Sample databases from provided data
const sampleDatabases = [
  {
    id: "db_1",
    name: "–£–¢ 11.4.16.242",
    connectionString: "Srvr=srv-1c-01;Ref=UT_Base;Usr=admin;Pwd=***;",
    server: "srv-1c-01",
    database: "UT_Base",
    status: "completed",
    lastAnalysis: "2024-10-04 10:45:00",
    configuration: {
      name: "–£–¢ 11.4.16.242",
      version: "11.4.16.242",
      extensions: ["–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ü–æ–¥–∫–ª—é—á–∞–µ–º–æ–≥–æ–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è", "–ò–Ω—Ç–µ–≥—Ä–∞—Ü–∏—è–°1–°–î–æ–∫—É–º–µ–Ω—Ç–æ–æ–±–æ—Ä–æ—Ç", "–ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏"]
