    overriddenMetadata: [
      {
        extensionName: "–ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏",
        items: [
          {
            type: "–°–ø—Ä–∞–≤–æ—á–Ω–∏–∫–∏",
            children: [
              {name: "–ù–æ–º–µ–Ω–∫–ª–∞—Ç—É—Ä–∞", status: "modified", changes: ["–î–æ–±–∞–≤–ª–µ–Ω —Ä–µ–∫–≤–∏–∑–∏—Ç –í–Ω–µ—à–Ω–∏–π–ö–æ–¥", "–ò–∑–º–µ–Ω–µ–Ω–∞ —Ñ–æ—Ä–º–∞ —ç–ª–µ–º–µ–Ω—Ç–∞"]},
              {name: "–ö–æ–Ω—Ç—Ä–∞–≥–µ–Ω—Ç—ã", status: "modified", changes: ["–î–æ–±–∞–≤–ª–µ–Ω–∞ —Ç–∞–±–ª–∏—á–Ω–∞—è —á–∞—Å—Ç—å –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ–†–µ–∫–≤–∏–∑–∏—Ç—ã"]}
            ]
          },
          {
            type: "–î–æ–∫—É–º–µ–Ω—Ç—ã",
            children: [
              {name: "–ü—Ä–∏—Ö–æ–¥–Ω–∞—è–ù–∞–∫–ª–∞–¥–Ω–∞—è", status: "modified", changes: ["–ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∞ –ø—Ä–æ—Ü–µ–¥—É—Ä–∞ –ü—Ä–∏–ó–∞–ø–∏—Å–∏"]},
              {name: "–†–∞—Å—Ö–æ–¥–Ω–∞—è–ù–∞–∫–ª–∞–¥–Ω–∞—è", status: "added", changes: ["–ù–æ–≤—ã–π –¥–æ–∫—É–º–µ–Ω—Ç –¥–ª—è —É—á–µ—Ç–∞ —Ä–∞—Å—Ö–æ–¥–æ–≤"]}
            ]
          }
        ]
      }
    ],
    overriddenFunctions: [
      {
        moduleType: "–û–±—â–∏–µ –º–æ–¥—É–ª–∏",
        modules: [
          {
            name: "–†–∞–±–æ—Ç–∞–°–§–∞–π–ª–∞–º–∏",
            functions: [
              {name: "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å–§–∞–π–ª–ù–∞–î–∏—Å–∫", status: "modified"},
              {name: "–ü–æ–ª—É—á–∏—Ç—å–ü—É—Ç—å–ö–§–∞–π–ª—É", status: "modified"}
            ]
          }
        ]
      }
    ]
  },
  {
    id: "db_2",
    name: "ERP 3.0.74.53",
    connectionString: "Srvr=srv-1c-02;Ref=ERP_Base;Usr=user;Pwd=***;",
    server: "srv-1c-02",
    database: "ERP_Base",
    status: "analyzing",
    progress: 65,
    currentStep: "–ê–Ω–∞–ª–∏–∑ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π...",
    configuration: {
      name: "ERP 3.0.74.53",
      version: "3.0.74.53",
      extensions: ["–ò–Ω—Ç–µ–≥—Ä–∞—Ü–∏—è1–°–î–æ–∫—É–º–µ–Ω—Ç–æ–æ–±–æ—Ä–æ—Ç", "–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ë—é–¥–∂–µ—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ"]
    }
  },
  {
    id: "db_3",
    name: "–†–æ–∑–Ω–∏—Ü–∞ 2.3.15.128",
    connectionString: "Srvr=srv-1c-03;Ref=Retail_Base;Usr=manager;Pwd=***;",
    server: "srv-1c-03",
    database: "Retail_Base",
    status: "connected",
    configuration: {
      name: "–†–æ–∑–Ω–∏—Ü–∞ 2.3.15.128",
      version: "2.3.15.128",
      extensions: ["–†–∞—Å—à–∏—Ä–µ–Ω–∏–µ–ü–æ–¥–∫–ª—é—á–∞–µ–º–æ–≥–æ–û–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è", "–ö–∞—Å—Å–æ–≤–∞—è–°–º–µ–Ω–∞"]
    }
  }
];

const analysisLog = [
  "–ù–∞—á–∞–ª–æ –∞–Ω–∞–ª–∏–∑–∞ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö",
  "–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ —Å–µ—Ä–≤–µ—Ä—É 1–°...",
  "–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ",
  "–í—ã–≥—Ä—É–∑–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏...",
  "–ö–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—è –≤—ã–≥—Ä—É–∂–µ–Ω–∞ (–£–¢ 11.4.16.242)",
  "–ü–æ–∏—Å–∫ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—ã—Ö —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π...",
  "–ù–∞–π–¥–µ–Ω–æ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–π: 3",
  "–ê–Ω–∞–ª–∏–∑ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏—è: –ö–∞—Å—Ç–æ–º–Ω—ã–µ–î–æ—Ä–∞–±–æ—Ç–∫–∏",
  "–ù–∞–π–¥–µ–Ω–æ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –æ–±—ä–µ–∫—Ç–æ–≤: 5",
  "–ê–Ω–∞–ª–∏–∑ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –ø—Ä–æ—Ü–µ–¥—É—Ä –∏ —Ñ—É–Ω–∫—Ü–∏–π...",
  "–ù–∞–π–¥–µ–Ω–æ –ø–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã—Ö –º–µ—Ç–æ–¥–æ–≤: 6",
  "–ê–Ω–∞–ª–∏–∑ –∑–∞–≤–µ—Ä—à–µ–Ω —É—Å–ø–µ—à–Ω–æ"
];

// Results View Component
const ResultsView = ({ database }) => {
  if (!database.overriddenMetadata || !database.overriddenFunctions) {
    return (
        <div className="empty-state">
          <div className="empty-state-icon">üìä</div>
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –∞–Ω–∞–ª–∏–∑–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω—ã</h2>
          <p>–î–∞–Ω–Ω—ã–µ –∞–Ω–∞–ª–∏–∑–∞ –¥–ª—è —ç—Ç–æ–π –±–∞–∑—ã –µ—â–µ –Ω–µ –≥–æ—Ç–æ–≤—ã.</p>
        </div>
    );
  }

  return (
      <div className="results-view">
        <div className="results-header">
          <h1>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã –∞–Ω–∞–ª–∏–∑–∞: {database.name}</h1>
          <div className="completion-time">
            –ê–Ω–∞–ª–∏–∑ –∑–∞–≤–µ—Ä—à–µ–Ω: {database.lastAnalysis || new Date().toLocaleString('ru-RU')}
          </div>
        </div>

        <div className="results-grid">
          {/* Configuration Info */}
          <div className="result-card config-info">
            <h3>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏</h3>
            <div className="config-details">
              <div className="config-detail">
                <div className="config-detail-label">–ù–∞–∑–≤–∞–Ω–∏–µ</div>
                <div className="config-detail-value">{database.configuration.name}</div>
              </div>
              <div className="config-detail">
                <div className="config-detail-label">–í–µ—Ä—Å–∏—è</div>
                <div className="config-detail-value">{database.configuration.version}</div>
              </div>
              <div className="config-detail">
                <div className="config-detail-label">–†–∞—Å—à–∏—Ä–µ–Ω–∏–π</div>
                <div className="config-detail-value">{database.configuration.extensions.length}</div>
              </div>
            </div>
            <div>
              <div className="config-detail-label">–£—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—ã–µ —Ä–∞—Å—à–∏—Ä–µ–Ω–∏—è:</div>
              <div className="extensions-list">
                {database.configuration.extensions.map((ext, index) => (
                    <span key={index} className="extension-tag">{ext}</span>
                ))}
              </div>
            </div>
          </div>

          {/* Overridden Metadata */}
          <div className="result-card">
            <h3>–ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã–µ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ</h3>
            <div className="tree-container">
              {database.overriddenMetadata.map((extension, index) => (
                  <div key={index}>
                    <TreeNode
                        node={{ name: extension.extensionName, children: extension.items }}
                        type="folder"
                    />
                  </div>
              ))}
            </div>
          </div>

          {/* Overridden Functions */}
          <div className="result-card">
            <h3>–ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ –∏ –ø—Ä–æ—Ü–µ–¥—É—Ä—ã</h3>
            <div className="tree-container">
              {database.overriddenFunctions.map((moduleType, index) => (
                  <TreeNode
                      key={index}
                      node={moduleType}
                      type="folder"
                  />
              ))}
            </div>
          </div>
        </div>
      </div>
  );
};

const Home = () => {
  const [databases, setDatabases] = useState(sampleDatabases);
  const [activeDatabaseId, setActiveDatabaseId] = useState('db_1');
  const [showModal, setShowModal] = useState(false);

  const addDatabase = (dbData) => {
    const newDatabase = {
      id: `db_${Date.now()}`,
      name: dbData.name,
      connectionString: dbData.connectionString,
      server: dbData.server,
      database: dbData.database,
      status: 'connecting',
      configuration: {
        name: dbData.name,
        version: '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ',
        extensions: []
      }
    };

    setDatabases(prev => [...prev, newDatabase]);
    console.log('–î–æ–±–∞–≤–ª–µ–Ω–∞ –Ω–æ–≤–∞—è –±–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö:', newDatabase);

    // Simulate connection process
    setTimeout(() => {
      setDatabases(prev => prev.map(db =>
          db.id === newDatabase.id
              ? { ...db, status: 'connected' }
              : db
      ));
      console.log(`–ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö ${newDatabase.name} –ø–æ–¥–∫–ª—é—á–µ–Ω–∞`);
    }, 2000);
  };

  const removeDatabase = (id) => {
    const database = databases.find(db => db.id === id);
    setDatabases(prev => prev.filter(db => db.id !== id));
    console.log(`–ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö —É–¥–∞–ª–µ–Ω–∞: ${database?.name}`);

    if (activeDatabaseId === id) {
      const remainingDbs = databases.filter(db => db.id !== id);
      setActiveDatabaseId(remainingDbs.length > 0 ? remainingDbs[0].id : null);
    }
  };

  const startAnalysis = (id) => {
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? { ...db, status: 'analyzing', progress: 0, currentStep: '–ù–∞—á–∞–ª–æ –∞–Ω–∞–ª–∏–∑–∞...' }
            : db
    ));
    console.log(`–ù–∞—á–∞—Ç –∞–Ω–∞–ª–∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö: ${databases.find(db => db.id === id)?.name}`);
  };

  const completeAnalysis = (id) => {
    const now = new Date().toLocaleString('ru-RU');
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? {
              ...db,
              status: 'completed',
              lastAnalysis: now,
              // Add mock analysis results if not present
              overriddenMetadata: db.overriddenMetadata || sampleDatabases[0].overriddenMetadata,
              overriddenFunctions: db.overriddenFunctions || sampleDatabases[0].overriddenFunctions
            }
            : db
    ));
    console.log(`–ê–Ω–∞–ª–∏–∑ –∑–∞–≤–µ—Ä—à–µ–Ω –¥–ª—è –±–∞–∑—ã: ${databases.find(db => db.id === id)?.name}`);
  };

  const cancelAnalysis = (id) => {
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? { ...db, status: 'connected', progress: 0 }
            : db
    ));
    console.log(`–ê–Ω–∞–ª–∏–∑ –æ—Ç–º–µ–Ω–µ–Ω –¥–ª—è –±–∞–∑—ã: ${databases.find(db => db.id === id)?.name}`);
  };

  const activeDatabase = databases.find(db => db.id === activeDatabaseId);

  const renderMainContent = () => {
    if (!activeDatabase) {
      return (
          <div className="empty-state">
            <div className="empty-state-icon">üóÑÔ∏è</div>
            <h2>–ù–µ—Ç –ø–æ–¥–∫–ª—é—á–µ–Ω–Ω—ã—Ö –±–∞–∑ –¥–∞–Ω–Ω—ã—Ö</h2>
            <p>–î–æ–±–∞–≤—å—Ç–µ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö 1–° –¥–ª—è –Ω–∞—á–∞–ª–∞ –∞–Ω–∞–ª–∏–∑–∞</p>
            <button
                className="btn btn--primary btn--lg"
                onClick={() => setShowModal(true)}
            >
              –î–æ–±–∞–≤–∏—Ç—å –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö
            </button>
          </div>
      );
    }

    if (activeDatabase.status === 'analyzing') {
      return (
          <AnalysisView
              database={activeDatabase}
              onComplete={completeAnalysis}
              onCancel={cancelAnalysis}
          />
      );
