import React from "react";
import { useDatabase } from "./DatabaseContext";
import {getConfigurationInfo } from '../services/configuration';
import {useQuery, useQueryClient} from '@tanstack/react-query';
import AnalysisView from "./AnalysisViewComponent";
import ResultsView from "./ResultsViewComponent";
import {handleStartAnalysis} from './DatabaseItemComponent'
import {BDStates} from "./States";

'./States'


const MainContent = () => {
    const { selectedDb, setSelectedDb } = useDatabase();
    const queryClient = useQueryClient();

    const { data: configuration, isLoading, error } = useQuery({
        queryKey: ['configuration', selectedDb?.ID],
        queryFn: ({queryKey }) => {
            const [, id] = queryKey;
            return getConfigurationInfo(id)
        } ,
        select: (data) => data.data,
        enabled: !!selectedDb, // запрос выполняется только если selectedDb есть
    });
    if(error) {
        console.log(error)
        return null
    }

    if (selectedDb?.Status === BDStates.ANALYZING) {
        return (
            <AnalysisView
                database={selectedDb}
                onComplete={() => {
                    setSelectedDb({...selectedDb, Status: BDStates.DONE})
                }}
                onCancel={() => {}}
            />
        );
    }

    if (selectedDb?.Status === BDStates.DONE) {
        return <ResultsView conf={configuration} database={selectedDb} />;
    }

    return (
        <div className="empty-state">
            <div className="empty-state-icon">⚡</div>

            {!selectedDb && (<p>Выберите базу данных из списка слева</p>)}
            {selectedDb && (
                <div>
                    <h2>{selectedDb?.Name}</h2>
                    <p>База данных и готова к анализу</p>
                </div>
            )}

            {selectedDb?.Status === BDStates.NEW && (
                <button
                    className="btn btn--primary btn--lg"
                    onClick={(e) => { handleStartAnalysis(e, selectedDb, queryClient, setSelectedDb)}}
                >
                    Начать анализ
                </button>
            )}
        </div>
    );
}

export default MainContent;
