import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import './Home.css';


const { useState, useEffect, useRef } = React;

// Sample databases from provided data
const sampleDatabases = [
  {
    id: "db_1",
    name: "УТ 11.4.16.242",
    connectionString: "Srvr=srv-1c-01;Ref=UT_Base;Usr=admin;Pwd=***;",
    server: "srv-1c-01",
    database: "UT_Base",
    status: "completed",
    lastAnalysis: "2024-10-04 10:45:00",
    configuration: {
      name: "УТ 11.4.16.242",
      version: "11.4.16.242",
      extensions: ["РасширениеПодключаемогоОборудования", "ИнтеграцияС1СДокументооборот", "КастомныеДоработки"]
    },
    overriddenMetadata: [
      {
        extensionName: "КастомныеДоработки",
        items: [
          {
            type: "Справочники",
            children: [
              {name: "Номенклатура", status: "modified", changes: ["Добавлен реквизит ВнешнийКод", "Изменена форма элемента"]},
              {name: "Контрагенты", status: "modified", changes: ["Добавлена табличная часть ДополнительныеРеквизиты"]}
            ]
          },
          {
            type: "Документы",
            children: [
              {name: "ПриходнаяНакладная", status: "modified", changes: ["Переопределена процедура ПриЗаписи"]},
              {name: "РасходнаяНакладная", status: "added", changes: ["Новый документ для учета расходов"]}
            ]
          }
        ]
      }
    ],
    overriddenFunctions: [
      {
        moduleType: "Общие модули",
        modules: [
          {
            name: "РаботаСФайлами",
            functions: [
              {name: "СохранитьФайлНаДиск", status: "modified"},
              {name: "ПолучитьПутьКФайлу", status: "modified"}
            ]
          }
        ]
      }
    ]
  },
  {
    id: "db_2",
    name: "ERP 3.0.74.53",
    connectionString: "Srvr=srv-1c-02;Ref=ERP_Base;Usr=user;Pwd=***;",
    server: "srv-1c-02",
    database: "ERP_Base",
    status: "analyzing",
    progress: 65,
    currentStep: "Анализ расширений...",
    configuration: {
      name: "ERP 3.0.74.53",
      version: "3.0.74.53",
      extensions: ["Интеграция1СДокументооборот", "РасширениеБюджетирование"]
    }
  },
  {
    id: "db_3",
    name: "Розница 2.3.15.128",
    connectionString: "Srvr=srv-1c-03;Ref=Retail_Base;Usr=manager;Pwd=***;",
    server: "srv-1c-03",
    database: "Retail_Base",
    status: "connected",
    configuration: {
      name: "Розница 2.3.15.128",
      version: "2.3.15.128",
      extensions: ["РасширениеПодключаемогоОборудования", "КассоваяСмена"]
    }
  }
];

const analysisLog = [
  "Начало анализа базы данных",
  "Подключение к серверу 1С...",
  "Подключение установлено успешно",
  "Выгрузка конфигурации...",
  "Конфигурация выгружена (УТ 11.4.16.242)",
  "Поиск установленных расширений...",
  "Найдено расширений: 3",
  "Анализ расширения: КастомныеДоработки",
  "Найдено переопределенных объектов: 5",
  "Анализ переопределенных процедур и функций...",
  "Найдено переопределенных методов: 6",
  "Анализ завершен успешно"
];

// TreeNode component for expandable tree structure
const TreeNode = ({ node, level = 0, type = 'folder' }) => {
  const [expanded, setExpanded] = useState(false);
  const [showDetails, setShowDetails] = useState(false);

  const handleToggle = () => {
    setExpanded(!expanded);
  };

  const hasChildren = node.children && node.children.length > 0;
  const hasFunctions = node.functions && node.functions.length > 0;
  const hasModules = node.modules && node.modules.length > 0;

  return (
      <div className="tree-node">
        <div className="tree-item" onClick={handleToggle}>
          <div className="tree-toggle">
            {(hasChildren || hasFunctions || hasModules) ? (expanded ? '▼' : '▶') : ''}
          </div>
          <div className={`tree-icon ${type}`}>
            {type === 'folder' ? '📁' : type === 'module' ? '⚙️' : '📄'}
          </div>
          <div className="tree-label">
            {node.name || node.type || node.moduleType}
          </div>
          {node.status && (
              <div className={`tree-status ${node.status}`}>
                {node.status === 'modified' ? 'Изменен' :
                    node.status === 'added' ? 'Добавлен' : 'Удален'}
              </div>
          )}
        </div>

        {expanded && (
            <div className="tree-children">
              {hasChildren && node.children.map((child, index) => (
                  <TreeNode key={index} node={child} level={level + 1} type="item" />
              ))}
              {hasModules && node.modules.map((module, index) => (
                  <TreeNode key={index} node={module} level={level + 1} type="module" />
              ))}
              {hasFunctions && node.functions.map((func, index) => (
                  <TreeNode key={index} node={func} level={level + 1} type="item" />
              ))}
            </div>
        )}

        {expanded && node.changes && (
            <div className="tree-details">
              <strong>Изменения:</strong>
              <ul>
                {node.changes.map((change, index) => (
                    <li key={index}>{change}</li>
                ))}
              </ul>
            </div>
        )}
      </div>
  );
};

// Database Form Modal
const DatabaseFormModal = ({ isOpen, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    server: '',
    database: '',
    username: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim() || !formData.server.trim() || !formData.database.trim()) {
      setError('Пожалуйста, заполните все обязательные поля');
      return;
    }

    const connectionString = `Srvr=${formData.server};Ref=${formData.database};Usr=${formData.username};Pwd=${formData.password};`;

    onSubmit({
      name: formData.name,
      server: formData.server,
      database: formData.database,
      connectionString
    });

    setFormData({ name: '', server: '', database: '', username: '', password: '' });
    setError('');
    onClose();
  };

  const handleClose = () => {
    setFormData({ name: '', server: '', database: '', username: '', password: '' });
    setError('');
    onClose();
  };

  if (!isOpen) return null;

  return (
      <div className="modal">
        <div className="modal-content">
          <div className="modal-header">
            <h2 className="modal-title">Добавить базу данных</h2>
            <button className="close-btn" onClick={handleClose}>×</button>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Название базы *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="Управление торговлей 11.4"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Сервер *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="srv-1c-01"
                  value={formData.server}
                  onChange={(e) => setFormData({ ...formData, server: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">База данных *</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="UT_Base"
                  value={formData.database}
                  onChange={(e) => setFormData({ ...formData, database: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Пользователь</label>
              <input
                  type="text"
                  className="connection-input"
                  placeholder="admin"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Пароль</label>
              <input
                  type="password"
                  className="connection-input"
                  placeholder="password"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              />
            </div>

            {error && <div style={{ color: 'var(--color-error)', marginBottom: '16px', fontSize: '14px' }}>{error}</div>}

            <div className="form-actions">
              <button type="button" className="btn btn--secondary" onClick={handleClose}>
                Отмена
              </button>
              <button type="submit" className="btn btn--primary">
                Добавить
              </button>
            </div>
          </form>
        </div>
      </div>
  );
};

// Database Item Component
const DatabaseItem = ({ database, isActive, onClick, onRemove, onStartAnalysis }) => {
  const getStatusText = () => {
    switch (database.status) {
      case 'connecting': return 'Подключение...';
      case 'connected': return 'Подключена';
      case 'analyzing': return 'Анализ...';
      case 'completed': return 'Завершен';
      case 'error': return 'Ошибка';
      default: return 'Неизвестно';
    }
  };

  const handleStartAnalysis = (e) => {
    e.stopPropagation();
    onStartAnalysis(database.id);
  };

  return (
      <div
          className={`database-item ${isActive ? 'active' : ''}`}
          onClick={() => onClick(database.id)}
      >
        <div className="database-actions">
          <button
              className="remove-btn"
              onClick={(e) => {
                e.stopPropagation();
                onRemove(database.id);
              }}
              title="Удалить базу"
          >
            ×
          </button>
        </div>

        <div className="database-item-header">
          <div className="database-name">{database.name}</div>
          <div className="database-status">
            <div className={`status-indicator ${database.status}`}></div>
            <span>{getStatusText()}</span>
          </div>
        </div>

        <div className="database-details">
          {database.server} / {database.database}
          {database.lastAnalysis && (
              <div>Анализ: {database.lastAnalysis}</div>
          )}
        </div>

        {database.status === 'analyzing' && database.progress && (
            <div className="database-progress">
              <div
                  className="progress-fill"
                  style={{ width: `${database.progress}%` }}
              ></div>
            </div>
        )}

        {database.status === 'connected' && (
            <button
                className="btn btn--primary btn--sm"
                onClick={handleStartAnalysis}
                style={{ marginTop: '8px', width: '100%' }}
            >
              Начать анализ
            </button>
        )}
      </div>
  );
};

// Analysis View Component
const AnalysisView = ({ database, onComplete, onCancel }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [logs, setLogs] = useState([]);
  const [showLogs, setShowLogs] = useState(false);
  const [progress, setProgress] = useState(0);
  const logEndRef = useRef(null);

  const steps = [
    "Подключение к серверу 1С...",
    "Выгрузка конфигурации...",
    "Анализ расширений...",
    "Обработка метаданных..."
  ];

  useEffect(() => {
    console.log(`Начало анализа базы данных: ${database.name}`);

    const analysisTimer = setTimeout(() => {
      let logIndex = 0;
      const logInterval = setInterval(() => {
        if (logIndex < analysisLog.length) {
          setLogs(prev => [...prev, analysisLog[logIndex]]);
          setProgress(Math.floor((logIndex + 1) / analysisLog.length * 100));
          logIndex++;
        } else {
          clearInterval(logInterval);
          console.log(`Анализ завершен для базы: ${database.name}`);
          setTimeout(() => onComplete(database.id), 1000);
        }
      }, 300);

      const stepInterval = setInterval(() => {
        setCurrentStep(prev => {
          if (prev < steps.length - 1) {
            return prev + 1;
          } else {
            clearInterval(stepInterval);
            return prev;
          }
        });
      }, 800);

      return () => {
        clearInterval(logInterval);
        clearInterval(stepInterval);
      };
    }, 500);

    return () => clearTimeout(analysisTimer);
  }, [database.id]);

  useEffect(() => {
    if (logEndRef.current) {
      logEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [logs]);

  return (
      <div className="analysis-view">
        <div className="analysis-status">
          <div className="spinner"></div>
          <div className="status-text">Анализ базы: {database.name}</div>
          <div className="substatus-text">{steps[currentStep]}</div>
          <div className="substatus-text">Прогресс: {progress}%</div>
          <button className="btn btn--cancel" onClick={() => onCancel(database.id)}>
            Отменить анализ
          </button>
        </div>

        <div className="log-section">
          <div className="card">
            <button
                className="btn btn--secondary log-toggle"
                onClick={() => setShowLogs(!showLogs)}
            >
              {showLogs ? 'Скрыть лог анализа' : 'Показать лог анализа'}
            </button>

            {showLogs && (
                <div className="log-viewer">
                  {logs.map((log, index) => (
                      <div key={index} className="log-entry">
                        {log}
                      </div>
                  ))}
                  <div ref={logEndRef} />
                </div>
            )}
          </div>
        </div>
      </div>
  );
};

// Results View Component
const ResultsView = ({ database }) => {
  if (!database.overriddenMetadata || !database.overriddenFunctions) {
    return (
        <div className="empty-state">
          <div className="empty-state-icon">📊</div>
          <h2>Результаты анализа недоступны</h2>
          <p>Данные анализа для этой базы еще не готовы.</p>
        </div>
    );
  }

  return (
      <div className="results-view">
        <div className="results-header">
          <h1>Результаты анализа: {database.name}</h1>
          <div className="completion-time">
            Анализ завершен: {database.lastAnalysis || new Date().toLocaleString('ru-RU')}
          </div>
        </div>

        <div className="results-grid">
          {/* Configuration Info */}
          <div className="result-card config-info">
            <h3>Информация о конфигурации</h3>
            <div className="config-details">
              <div className="config-detail">
                <div className="config-detail-label">Название</div>
                <div className="config-detail-value">{database.configuration.name}</div>
              </div>
              <div className="config-detail">
                <div className="config-detail-label">Версия</div>
                <div className="config-detail-value">{database.configuration.version}</div>
              </div>
              <div className="config-detail">
                <div className="config-detail-label">Расширений</div>
                <div className="config-detail-value">{database.configuration.extensions.length}</div>
              </div>
            </div>
            <div>
              <div className="config-detail-label">Установленные расширения:</div>
              <div className="extensions-list">
                {database.configuration.extensions.map((ext, index) => (
                    <span key={index} className="extension-tag">{ext}</span>
                ))}
              </div>
            </div>
          </div>

          {/* Overridden Metadata */}
          <div className="result-card">
            <h3>Переопределенные метаданные</h3>
            <div className="tree-container">
              {database.overriddenMetadata.map((extension, index) => (
                  <div key={index}>
                    <TreeNode
                        node={{ name: extension.extensionName, children: extension.items }}
                        type="folder"
                    />
                  </div>
              ))}
            </div>
          </div>

          {/* Overridden Functions */}
          <div className="result-card">
            <h3>Переопределенные функции и процедуры</h3>
            <div className="tree-container">
              {database.overriddenFunctions.map((moduleType, index) => (
                  <TreeNode
                      key={index}
                      node={moduleType}
                      type="folder"
                  />
              ))}
            </div>
          </div>
        </div>
      </div>
  );
};

const Home = () => {

  const [databases, setDatabases] = useState(sampleDatabases);
  const [activeDatabaseId, setActiveDatabaseId] = useState('db_1');
  const [showModal, setShowModal] = useState(false);

  // Initialize theme
  useEffect(() => {
    // Simulate theme persistence (would use localStorage in real app)
    const savedTheme = 'light'; // localStorage.getItem('theme') || 'light';
    setTheme(savedTheme);
    document.documentElement.setAttribute('data-color-scheme', savedTheme);
    console.log(`Тема инициализирована: ${savedTheme}`);
  }, []);

  const addDatabase = (dbData) => {
    const newDatabase = {
      id: `db_${Date.now()}`,
      name: dbData.name,
      connectionString: dbData.connectionString,
      server: dbData.server,
      database: dbData.database,
      status: 'connecting',
      configuration: {
        name: dbData.name,
        version: 'Неизвестно',
        extensions: []
      }
    };

    setDatabases(prev => [...prev, newDatabase]);
    console.log('Добавлена новая база данных:', newDatabase);

    // Simulate connection process
    setTimeout(() => {
      setDatabases(prev => prev.map(db =>
          db.id === newDatabase.id
              ? { ...db, status: 'connected' }
              : db
      ));
      console.log(`База данных ${newDatabase.name} подключена`);
    }, 2000);
  };

  const removeDatabase = (id) => {
    const database = databases.find(db => db.id === id);
    setDatabases(prev => prev.filter(db => db.id !== id));
    console.log(`База данных удалена: ${database?.name}`);

    if (activeDatabaseId === id) {
      const remainingDbs = databases.filter(db => db.id !== id);
      setActiveDatabaseId(remainingDbs.length > 0 ? remainingDbs[0].id : null);
    }
  };

  const startAnalysis = (id) => {
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? { ...db, status: 'analyzing', progress: 0, currentStep: 'Начало анализа...' }
            : db
    ));
    console.log(`Начат анализ базы данных: ${databases.find(db => db.id === id)?.name}`);
  };

  const completeAnalysis = (id) => {
    const now = new Date().toLocaleString('ru-RU');
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? {
              ...db,
              status: 'completed',
              lastAnalysis: now,
              // Add mock analysis results if not present
              overriddenMetadata: db.overriddenMetadata || sampleDatabases[0].overriddenMetadata,
              overriddenFunctions: db.overriddenFunctions || sampleDatabases[0].overriddenFunctions
            }
            : db
    ));
    console.log(`Анализ завершен для базы: ${databases.find(db => db.id === id)?.name}`);
  };

  const cancelAnalysis = (id) => {
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? { ...db, status: 'connected', progress: 0 }
            : db
    ));
    console.log(`Анализ отменен для базы: ${databases.find(db => db.id === id)?.name}`);
  };

  const activeDatabase = databases.find(db => db.id === activeDatabaseId);

  const renderMainContent = () => {
    if (!activeDatabase) {
      return (
          <div className="empty-state">
            <div className="empty-state-icon">🗄️</div>
            <h2>Нет подключенных баз данных</h2>
            <p>Добавьте базу данных 1С для начала анализа</p>
            <button
                className="btn btn--primary btn--lg"
                onClick={() => setShowModal(true)}
            >
              Добавить базу данных
            </button>
          </div>
      );
    }

    if (activeDatabase.status === 'analyzing') {
      return (
          <AnalysisView
              database={activeDatabase}
              onComplete={completeAnalysis}
              onCancel={cancelAnalysis}
          />
      );
    }

    if (activeDatabase.status === 'completed') {
      return <ResultsView database={activeDatabase} />;
    }

    return (
        <div className="empty-state">
          <div className="empty-state-icon">⚡</div>
          <h2>{activeDatabase.name}</h2>
          <p>База данных подключена и готова к анализу</p>
          {activeDatabase.status === 'connected' && (
              <button
                  className="btn btn--primary btn--lg"
                  onClick={() => startAnalysis(activeDatabase.id)}
              >
                Начать анализ
              </button>
          )}
        </div>
    );
  };

  return (
      <div className="app">

        <div className="main-layout">
          <aside className="sidebar">
            <div className="sidebar-header">
              <h2 className="sidebar-title">Базы данных</h2>
              <button
                  className="add-database-btn"
                  onClick={() => setShowModal(true)}
              >
                <span>+</span>
                Добавить базу
              </button>
            </div>

            <div className="sidebar-content">
              <div className="database-list">
                {databases.map(database => (
                    <DatabaseItem
                        key={database.id}
                        database={database}
                        isActive={database.id === activeDatabaseId}
                        onClick={setActiveDatabaseId}
                        onRemove={removeDatabase}
                        onStartAnalysis={startAnalysis}
                    />
                ))}
              </div>
            </div>
          </aside>

          <main className="main-content">
            {renderMainContent()}
          </main>
        </div>

        <DatabaseFormModal
            isOpen={showModal}
            onClose={() => setShowModal(false)}
            onSubmit={addDatabase}
        />
      </div>
  );
};

export default Home;
