  const startAnalysis = (id) => {
    setDatabases(prev => prev.map(db =>
        db.id === id
            ? { ...db, status: 'analyzing', progress: 0, currentStep: '–ù–∞—á–∞–ª–æ –∞–Ω–∞–ª–∏–∑–∞...' }
