    ));
    console.log(`–ê–Ω–∞–ª–∏–∑ –æ—Ç–º–µ–Ω–µ–Ω –¥–ª—è –±–∞–∑—ã: ${databases.find(db => db.id === id)?.name}`);
  };

  const activeDatabase = databases.find(db => db.id === activeDatabaseId);

