import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { getBaseSettings } from '../services/settingservice';
import UserCard from '../components/UserCard';
import UserModal from '../components/UserModal';
import './Settings.css';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';


const Settings = () => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  const { data: settings, isLoading, error } = useQuery({
    queryKey: ['settings'],
    queryFn: getBaseSettings,
    select: (data) => data.data,
  });

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleCreateUser = () => {
    setSelectedUser(null);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedUser(null);
  };

  const handleOption1 = () => {
    alert("Создать PostgreSQL базу");
    setIsOpen(false); // закрыть меню после клика
  };

  const handleOption2 = () => {
    alert("Создать MySQL базу");
    setIsOpen(false);
  };


  if (isLoading) {
    return <div className="loading">Загрузка настроек...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error.message}</div>;
  }

  return (
    <div className="settings-page">
      <DropdownButton id="dropdown-basic-button" title="Dropdown button">
        <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
      </DropdownButton>

      <div className="settings-header">
        <h2>Подключение к базам</h2>
        <button onClick={ () =>  setIsOpen(!isOpen)} className="btn btn-primary">
          Добавить базу
        </button>
      </div>


      {isOpen && (
          <div
              className="dropdown-menu"
              style={{
                position: "absolute",
                top: "100%",
                left: 0,
                marginTop: "5px",
                background: "white",
                border: "1px solid #ddd",
                borderRadius: "6px",
                boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                zIndex: 100,
              }}
          >
            <button
                onClick={handleOption1}
                className="dropdown-item"
                style={{ display: "block", padding: "8px 12px", width: "100%", textAlign: "left" }}
            >
              PostgreSQL
            </button>
            <button
                onClick={handleOption2}
                className="dropdown-item"
                style={{ display: "block", padding: "8px 12px", width: "100%", textAlign: "left" }}
            >
              MySQL
            </button>
          </div>
      )}

      <div className="settings-list">
        {settings && settings.length > 0 ? (
          settings.map((user) => (
            <UserCard 
              key={user.id} 
              user={user} 
              onEdit={handleEditUser}
            />
          ))
        ) : (
          <div className="no-settings">
            <p>Настройки не найдены</p>
          </div>
        )}
      </div>

      {isModalOpen && (
        <UserModal 
          user={selectedUser}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default Settings;
