import React from "react";
import { getAppSettings, storeAppSettings } from '../services/appSettings';
import {useMutation, useQuery} from '@tanstack/react-query';
import {addBaseSettings} from "../services/settingservice";

const { useState, useEffect, useRef } = React;
const addSettings = useMutation({
    mutationFn: addBaseSettings,
    onSuccess: () => {
        setFormData({ name: '', connectionString: '', username: '', password: '' });
        setError('');
        setShowModal(false);

        queryClient.invalidateQueries({ // для перересовки компонентов
            queryKey: ['databases']
        }).then(r => {});
    },
    onError: (err) => {
        console.log(err)

        if (err instanceof Error) {
            setError('Request error: '+ err.message);
        } else {
            setError('Не удалось добавить базу');
        }
    }
});

const useLoadSettings = (setFormData) => {
    return useQuery({
        queryKey: ['settings'],
        queryFn: getAppSettings,
        select: (data) => data.data,
        refetchOnMount: 'always',
    });
}

const SettingsModal = ({onClose}) => {
    const [formData, setFormData] = useState(null);
    const {data: settings, isLoading, error } = useLoadSettings();
    if (error) {
        console.log(error)
    }
    useEffect(() => {
        if (settings) {
            setFormData(settings);
        }
    }, [settings]);


    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Сохранение глобальных настроек:');

        addSettings.mutate({
            PlatformPath: formData.PlatformPath,
        });

        {error && <div style={{ color: 'var(--color-error)', marginBottom: '16px', fontSize: '14px' }}>{error}</div>}


        onClose();
    };

    console.log(settings)

    return (
        <div className="modal modal-medium">
            <div className="modal-content">
                <div className="modal-header">
                    <h2 className="modal-title">Глобальные настройки</h2>
                    <button className="close-btn" onClick={onClose}>×</button>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="modal-body">
                        <div className="settings-form">
                            <div className="settings-section">
                                <h3>Путь к платформе 1С</h3>
                                <div className="form-group">
                                    <label className="form-label">Исполняемый файл платформы</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={settings?.PlatformPath}
                                        onChange={(e) => setFormData({ ...formData, PlatformPath: e.target.value })}
                                    />
                                </div>
                            </div>

                            {/*<div className="settings-section">*/}
                            {/*    <h3>Настройки подключения</h3>*/}
                            {/*    <div className="form-row">*/}
                            {/*        <div className="form-group">*/}
                            {/*            <label className="form-label">Таймаут подключения (сек)</label>*/}
                            {/*            <input*/}
                            {/*                type="number"*/}
                            {/*                className="form-control"*/}
                            {/*                value={formData.connectionTimeout}*/}
                            {/*                min="10"*/}
                            {/*                max="120"*/}
                            {/*            />*/}
                            {/*        </div>*/}
                            {/*        <div className="form-group">*/}
                            {/*            <label className="form-label">Таймаут анализа (сек)</label>*/}
                            {/*            <input*/}
                            {/*                type="number"*/}
                            {/*                className="form-control"*/}
                            {/*                value={formData.analysisTimeout}*/}
                            {/*                min="60"*/}
                            {/*                max="1800"*/}
                            {/*            />*/}
                            {/*        </div>*/}
                            {/*    </div>*/}
                            {/*</div>*/}
                        </div>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn--secondary" onClick={onClose}>
                            Отмена
                        </button>
                        <button type="submit" className="btn btn--primary">
                            Сохранить
                        </button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default SettingsModal;
