};

const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    document.documentElement.setAttribute('data-color-scheme', newTheme);
    console.log(`–¢–µ–º–∞ –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∞ –Ω–∞: ${newTheme}`);
};

const Header = () => {
    const [theme, setTheme] = useState('light');
    // Initialize theme
    useEffect(() => {
