const ScheduleEditorModal = ({ onClose, database }) => {
    const [autoAnalysisEnabled, setAutoAnalysisEnabled] = useState(database?.autoAnalysisEnabled || false);
    const [cronExpression, setCronExpression] = useState(database?.cronSchedule || '');
    const [selectedPreset, setSelectedPreset] = useState('');
    const [validationMessage, setValidationMessage] = useState('');

    useEffect(() => {
        if (database) {
            setAutoAnalysisEnabled(database.autoAnalysisEnabled || false);
            setCronExpression(database.cronSchedule || '');
        }
    }, [database]);

    const validateCronExpression = (expression) => {
        if (!expression.trim()) {
            setValidationMessage('');
            return false;
        }

        const parts = expression.trim().split(/\s+/);
        if (parts.length !== 5) {
            setValidationMessage('CRON выражение должно содержать 5 частей');
            return false;
        }

        setValidationMessage('Валидное CRON выражение');
        return true;
    };

    const handleCronChange = (value) => {
        setCronExpression(value);
        validateCronExpression(value);
        setSelectedPreset('');
    };

    const handlePresetSelect = (preset) => {
        setCronExpression(preset.expression);
        setSelectedPreset(preset.expression);
        validateCronExpression(preset.expression);
    };

    const handleSave = () => {
        const isValid = cronExpression.trim() === '' || validateCronExpression(cronExpression);
        if (!isValid && autoAnalysisEnabled) {
            return;
        }

        console.log('Сохранение расписания для базы:', database.id, {
            autoAnalysisEnabled,
            cronExpression
        });

        onSave({
            ...database,
            autoAnalysisEnabled,
            cronSchedule: cronExpression,
            nextRun: autoAnalysisEnabled && cronExpression ? '2024-10-05 02:00:00' : null
        });
        onClose();
    };

    if (!isOpen || !database) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <div className="modal-header">
                    <h2 className="modal-title">Настройка расписания анализа</h2>
                    <button className="close-btn" onClick={onClose}>×</button>
                </div>

                <div className="modal-body">
                    <div className="database-info">
                        <strong>База данных:</strong> {database.name}
                    </div>

                    <div className="checkbox-group">
                        <input
                            type="checkbox"
                            id="autoAnalysis"
                            checked={autoAnalysisEnabled}
                            onChange={(e) => setAutoAnalysisEnabled(e.target.checked)}
                        />
                        <label htmlFor="autoAnalysis">Включить автоматический анализ</label>
                    </div>

                    {autoAnalysisEnabled && (
                        <div className="cron-section">
                            <div className="form-group">
                                <label className="form-label">CRON выражение</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={cronExpression}
                                    onChange={(e) => handleCronChange(e.target.value)}
                                    placeholder="0 2 * * * (ежедневно в 2:00)"
                                />
                                {validationMessage && (
                                    <div className={`cron-validation ${validationMessage.includes('Валидное') ? 'valid' : 'invalid'}`}>
                                        {validationMessage}
                                    </div>
                                )}
                            </div>

                            <div className="form-group">
                                <label className="form-label">Готовые шаблоны</label>
                                <div className="cron-presets">
                                    {mockData.cronPresets.map((preset, index) => (
                                        <button
                                            key={index}
                                            className={`preset-btn ${selectedPreset === preset.expression ? 'selected' : ''}`}
                                            onClick={() => handlePresetSelect(preset)}
                                        >
                                            <div>{preset.name}</div>
                                            <div style={{ fontSize: '12px', color: 'var(--color-text-secondary)' }}>
                                                {preset.expression}
                                            </div>
                                        </button>
                                    ))}
                                </div>
                            </div>
                        </div>
                    )}
                </div>

                <div className="modal-footer">
                    <button className="btn btn--secondary" onClick={onClose}>
                        Отмена
                    </button>
                    <button className="btn btn--primary" onClick={handleSave}>
                        Сохранить
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ScheduleEditorModal;
