import React from "react";
import { useDatabase } from "./DatabaseContext";

const startAnalysis = (database) => {
    console.log(`Начат анализ базы данных: ${database.Name}`);
};

const MainContent = (database) => {
    const { selectedDb } = useDatabase();

    return (
        <div className="empty-state">
            <div className="empty-state-icon">⚡</div>

            {console.log(selectedDb)}

            {!selectedDb && (<p>Выберите базу данных из списка слева</p>)}
            {selectedDb && (
                <div>
                    <h2>{selectedDb?.Name}</h2>
                    <p>База данных и готова к анализу</p>
                </div>
            )}

            {selectedDb.Status === 'new' && (
                <button
                    className="btn btn--primary btn--lg"
                    onClick={() => startAnalysis(selectedDb)}
                >
                    Начать анализ
                </button>
            )}
        </div>
    );
}

export default MainContent;
